import React, { useState } from 'react';
import './nav.css';
import image02 from '../images/02.png';
import image16 from '../images/16.png';
import { useNavigate } from 'react-router-dom';

function Nav() {
    const [openProfile, setOpenProfile] = useState(false);
    const navigate = useNavigate();
    const id = 1; // You need to set the actual id value or get it from your context or props

    const handleDropdown = () => {
        setOpenProfile(prevState => !prevState); // Toggles the state
    };

    const handleCart = () => {
        navigate(`/cart/${id}`);
    };

    const handleProfileClick = (action) => {
        setOpenProfile(false);
        if (action === 'about') {
            // Navigate to About page or perform About action
        } else if (action === 'contact') {
            // Navigate to Contact page or perform Contact action
        } else if (action === 'logout') {
            // Perform logout action
        }
    };

    const DropdownProfile = () => (
        <div className='dropDownProfile'>
            <ul className='flex flex-col gap-4 ul02'>
                <li className='about02' onClick={() => handleProfileClick('about')}>About</li>
                <li className='contact02' onClick={() => handleProfileClick('contact')}>Contact</li>
                <li className='logout02' onClick={() => handleProfileClick('logout')}>Logout</li>
            </ul>
        </div>
    );

    return (
        <div className='navcontainer02 navbar-expand-lg navbar-light bg-light fixed-top'>
            <div className='nav02'>
                <img src={image02} alt='logo' height={50} />
                <h3 className='h302'>HOME</h3>
                <div className='navright02'>
                    <div onClick={handleCart}>
                        <img src={image16} alt='cart' className='img1602' height={35} />
                    </div>
                    <img 
                        className='img202' 
                        src='https://kalytapartners.com/images/icon-menu.png' 
                        alt='menu' 
                        height={30} 
                        onClick={handleDropdown} 
                    />
                    {openProfile && <DropdownProfile />}
                </div>
            </div>
            <hr />
        </div>
    );
}

export default Nav;
