import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function AdBookUpdate() {
  
 

  const handleCancel = (e) => {
    e.preventDefault();
    navigate('/newbookdata');
  };

  const handleRatingChange = (e) => {
    const value = Math.min(Math.max(0, parseInt(e.target.value)), 5);
    setRating(value);
  };

  return (
    <div className='full05'>
      <div className='container05'>
        <h2 className='h205'>BOOK UPDATE FORM</h2>
        <form encType="multipart/form-data" onSubmit={handleUpdate}>
       
        <div className='box05'>
                        <label htmlFor="image">Image:</label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            accept="image/*"
                            onChange={(e) => setFile(e.target.files[0])}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book ID:</label>
                        <input
                            name='id'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setId(e.target.value)}
                            value={id}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book Name:</label>
                        <input
                            name='name'
                            className='form-control rounded-0'
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                        />
                    </div>

                    <div className='box05'>
                        <label>Description:</label>
                        <input
                            name='description'
                            className='form-control rounded-0 two-line-input'
                            onChange={(e) => setDescription(e.target.value)}
                            value={description}
                        />
                    </div>

                    <div className='box05'>
                        <label>Rating:</label>
                        <input
                            name='rating'
                            type='number'
                            className='form-control rounded-0'
                            onChange={handleRatingChange}
                            value={rating}
                            min="0"
                            max="5"
                        />
                    </div>

                    <div className='box05'>
                        <label>Price:</label>
                        <input
                            name='price'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setPrice(e.target.value)}
                            value={price}
                        />
                    </div>

          <button type='submit' className='submit05'>Update</button>
          <button type='button' className='submit05' onClick={handleCancel}>Cancel</button>
        </form>
      </div>
    </div>
  );
}

export default AdBookUpdate;
