import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Nav from '../Nav';
import './home.css';
import image04 from '../../images/04.jpg';
import image05 from '../../images/08.png';
import image09 from '../../images/09.png';
import image10 from '../../images/10.png';
import image11 from '../../images/11.png';
import image12 from '../../images/12.png';
import image15 from '../../images/15.jpg';

function Home() {
  const [newbookData, setNewbookData] = useState([]);
  const [interbookData, setInterbookData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showAllNew, setShowAllNew] = useState(false);
  const [showAllInter, setShowAllInter] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const [newBooksResponse, interBooksResponse] = await Promise.all([
          axios.get('http://localhost:1500/getnewbook'),
          axios.get('http://localhost:1500/getinterbook'),
          axi
        ]);

        if (newBooksResponse.data && newBooksResponse.data.books) {
          setNewbookData(newBooksResponse.data.books);
        } else {
          console.error('Invalid new books response format:', newBooksResponse.data);
        }

        if (interBooksResponse.data && interBooksResponse.data.interbook) {
          setInterbookData(interBooksResponse.data.interbook);
        } else {
          console.error('Invalid international books response format:', interBooksResponse.data);
        }
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % 3); // Adjust based on the number of images
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const visibleNewBooks = showAllNew ? newbookData : newbookData.slice(0, 4);
  const visibleInterBooks = showAllInter ? interbookData : interbookData.slice(0, 4);
  const visibleBestsellerBooks = showAllBestseller ? bestsellerbookData : bestsellerbookData.slice(0, 4);

  const handleSeeAllNew = () => {
    setShowAllNew(true);
  };

  const handleSeeAllInter = () => {
    setShowAllInter(true);
    navigate('/international')
  };

  const handleSeeAllBestseller = () => {
    setShowAllInter(true);
    navigate('/bestsellerdata')
  };

  return (
    <div className='home-container'>
      <div className='nav03'>
        <Nav />
      </div>

      <div className='container03'>
        <div className="carousel" style={{ transform: `translateX(-${currentIndex * (100 / 3)}%)` }}>
          <img src={image04} alt='image04' />
          <img src={image05} alt='image05' />
          <img src={image15} alt='image15' />
        </div>
      </div>

      <hr className='hr03'></hr>
      <div className='badges03'>
        <div className='badge1'>
          <img src={image09} alt='image09' className='image09' />
          <h4>Award wins</h4>
        </div>
        <div className='badge2'>
          <img src={image10} alt='image10' className='image10' />
          <h4>International</h4>
        </div>
        <div className='badge3'>
          <img src={image11} alt='image11' className='image11' />
          <h4>Best seller</h4>
        </div>
        <div className='badge4'>
          <img src={image12} alt='image12' className='image12' />
          <h4>New Arrivals</h4>
        </div>
      </div>

      <h2>NEW ARRIVALS</h2>
      <div className='card-container03'>
        {visibleNewBooks.map((newbook, index) => (
          <div key={index} className='card03'>
            <img src={`http://localhost:1500/images/${newbook.image}`} alt='newbookimage' width={200} height={150} />
            <h3>{newbook.name}</h3>
            <p>{newbook.author}</p>
            <p className='description03'>{newbook.description}</p>
            <div className='rating03'>
              <img className='ratingstar03' src='http://clipart-library.com/img/2184497.png' alt='image' height={18} />
              <p className='ratingvalue03'>{newbook.rating}</p>
            </div>
            <p className='price03'>{newbook.price}</p>
            <button className='addtocartbutton03'>Add to cart</button>
            <button className='buynowbutton03'>Buy now</button>
          </div>
        ))}
      </div>
      {!showAllNew && (
        <button className='seeall03' onClick={handleSeeAllNew}>See All</button>
      )}

      <h2>INTERNATIONAL</h2>
      <div className='card-container03'>
        {visibleInterBooks.map((interbook, index) => (
          <div key={index} className='card03'>
            <img src={`http://localhost:1500/images/${interbook.image}`} alt='interbookimage' width={200} height={150} />
            <h3>{interbook.name}</h3>
            <p>{interbook.author}</p>
            <p className='description03'>{interbook.description}</p>
            <div className='rating03'>
              <img className='ratingstar03' src='http://clipart-library.com/img/2184497.png' alt='image' height={18} />
              <p className='ratingvalue03'>{interbook.rating}</p>
            </div>
            <p className='price03'>{interbook.price}</p>
            <button className='addtocartbutton03'>Add to cart</button>
            <button className='buynowbutton03'>Buy now</button>
          </div>
        ))}
      </div>
      {!showAllInter && (
        <button className='seeall03' onClick={handleSeeAllInter}>See All</button>
      )}

<h2>BEST SELLER</h2>
      <div className='card-container03'>
        {visibleBestsellerBooks.map((bestsellerbook, index) => (
          <div key={index} className='card03'>
            <img src={`http://localhost:1500/images/${bestsellerbook.image}`} alt='interbookimage' width={200} height={150} />
            <h3>{bestsellerbook.name}</h3>
            <p>{bestsellerbook.author}</p>
            <p className='description03'>{bestsellerbook.description}</p>
            <div className='rating03'>
              <img className='ratingstar03' src='http://clipart-library.com/img/2184497.png' alt='image' height={18} />
              <p className='ratingvalue03'>{bestsellerbook.rating}</p>
            </div>
            <p className='price03'>{bestsellerbook.price}</p>
            <button className='addtocartbutton03'>Add to cart</button>
            <button className='buynowbutton03'>Buy now</button>
          </div>
        ))}
      </div>
      {!showAllBestseller && (
        <button className='seeall03' onClick={handleSeeAllBestseller}>See All</button>
      )}
    </div>
  );
}

export default Home;
