import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Nav from '../../Nav';


function Feedback() {
  const [feedbackData, setFeedbackData] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFeedback = async () => {
      try {
        const response = await axios.get('http://localhost:1500/getfeedback');
        if (response.data) {
          setFeedbackData(response.data);
        } else {
          console.error('Invalid response format:', response.data);
        }
      } catch (error) {
        console.error('Error fetching feedback:', error);
        setError('Error fetching feedback: ' + error.message);
      }
    };

    fetchFeedback();
  }, []);

  return (
    <div className='full06'>
      <Nav />
      <br/> 
      <h2 className='h206'>Feedback</h2>
      <div className='table-container'>
        {error && <p className='error-message'>{error}</p>}
        <table className='styled-table'>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Feedback</th>
            </tr>
          </thead>
          <tbody>
            {feedbackData.map(userfeedback => (
              <tr key={userfeedback._id}>
                <td>{userfeedback.name}</td>
                <td>{userfeedback.email}</td>
                <td className='description05'>{userfeedback.feedback}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Feedback;
