// In AdBookUpdate component
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

function AdBookUpdate() {
    const { id } = useParams(); // Retrieve ID from URL
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [rating, setRating] = useState('');
    const [price, setPrice] = useState('');
    const [file, setFile] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:1500/getnewupdatebook/${id}`);
                const bookData = response.data.book;
                setName(bookData.name);
                setDescription(bookData.description);
                setRating(bookData.rating);
                setPrice(bookData.price);
            } catch (error) {
                console.error('Error fetching book data:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('image', file);
            formData.append('name', name);
            formData.append('description', description);
            formData.append('rating', rating);
            formData.append('price', price);

            const config = {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            };

            await axios.put(`http://localhost:1500/updatenewbook/${id}`, formData, config);
            navigate('/newbookdata');
        } catch (error) {
            console.error('Error updating book:', error);
        }
    };

    const handleCancel = (e) => {
        e.preventDefault();
        navigate('/newbookdata');
    };

    const handleRatingChange = (e) => {
        const value = Math.min(Math.max(0, parseInt(e.target.value)), 5);
        setRating(value);
    };

    return (
        <div className='full05'>
            <div className='container05'>
                <h2 className='h205'>BOOK UPDATE FORM</h2>
                <form encType="multipart/form-data">
                    {/* Input fields for book details */}
                    <button type='submit' className='submit05' onClick={handleUpdate}>Update</button>
                    <button type='button' className='submit05' onClick={handleCancel}>Cancel</button>
                </form>
            </div>
        </div>
    );
}

export default AdBookUpdate;
