import React, { useState } from 'react';
import { Link } from 'react-router-dom';
impo

function Signup() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Your signup form submission logic goes here
        console.log('Form submitted!');
    };
    axios.post('http://localhost:1500/signup')

    return (
        <div className='full01'>
            <h2 className='h201'>SIGNUP FORM</h2>
            <div className='container01'>
                <form className='form01' onSubmit={handleSubmit}>
                    <div className='box01'>
                        <label>Name:</label>
                        <input

                            autoComplete='on' // Allow browser to autocomplete name
                            name='name'
                            className='form-control rounded-0'
                            onChange={(e) => setName(e.target.value)}
                        />
                        <br />
                        <br />
                        <label> E-mail ID:</label>
                        <input

                            autoComplete='email' // Allow browser to autocomplete email
                            name='email'
                            className='form-control rounded-0'
                            onChange={(e) => setEmail(e.target.value)}
                        />
                        <br />
                        <br />
                        <label>Password:</label>
                        <input

                            autoComplete='off'
                            name='password'
                            type='password'
                            className='form-control rounded-0'
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <br />   
                        <br />
                        <button type='submit' className='signup01'>Signup</button>
                    </div>
                </form>
                <p className='q01'>Already have an account?</p>
                <Link to="/login" className='login01'>Login</Link>
            </div>
        </div>
    );
}

export default Signup;
