import React, { useState } from 'react';
import './Adbookform.css';

// StarRating component
const StarRating = ({ value, onChange }) => {
    const stars = [1, 2, 3, 4, 5];

    return (
        <div className="star-rating05">
            {stars.map((star) => (
                <label key={star}>
                    <input
                        type="radio"
                        name="rating"
                        value={star}
                        checked={value === star}
                        onChange={() => onChange(star)}
                    />
                    <span className="star">&#9733;</span>
                </label>
            ))}
        </div>
    );
};

function AdBookform() {
    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const [rating, setRating] = useState(0);
    const [price, setPrice] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Add your submit logic here
    };

    return (
        <div className='full05'>
            <div className='container05'>
                <h2 className='h205'>BOOK ENTRY FORM</h2>
                <form onSubmit={handleSubmit} className='form05'>
                    <div className='box05'>
                        <label>Book Name:</label>
                        <input
                            name='name'
                            className='form-control rounded-0'
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book ID:</label>
                        <input
                            name='id'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setId(e.target.value)}
                            value={id}
                        />
                    </div>

                    <div className='box05'>
                        <label>Rating:</label>
                        <StarRating value={rating} onChange={setRating} />
                    </div>

                    <div className='box05'>
                        <label>Price:</label>
                        <input
                            name='price'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setPrice(e.target.value)}
                            value={price}
                        />
                    </div>

                    <button type='submit' className='u05'>upload</button>
                </form>
            </div>
        </div>
    );
}

export default AdBookform;
