import React, { useState } from 'react';
import './Adbookform.css';
import axios from 'axios'
import { set } from 'mongoose';

function AdBookform() {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [id, setId] = useState('');
    const [rating, setRating] = useState('');
    const [price, setPrice] = useState('');
    const [file, setFile] = useState(null);
    const handleSubmit = async (e) => {
        e.preventDefault();
    

        try {
            const bookData = { name, id, description, rating, price };

            const response = await axios.post('http://localhost:1500/postnewbook', bookData);

            console.log('New book created:', response.data);
setName('');
setFile(null);
setId('');
setDescription('');
setRating('');
setPrice('');
            // Add logic here for success, like showing a success message or redirecting
        } catch (error) {
            console.error('Error creating new book:', error);
            // Add logic here for error handling, like showing an error message to the user
        }
    };

    const handleRatingChange = (e) => {
        const value = Math.min(Math.max(0, parseInt(e.target.value)), 5);
        setRating(value);
    };

    return (
        <div className='full05'>
            <div className='container05'>
                <h2 className='h205'>BOOK ENTRY FORM</h2>
                <form onSubmit={handleSubmit}>
                    <div className='box05'>
                        <label>Book ID:</label>
                        <input
                            name='id'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setId(e.target.value)}
                            value={id}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book Name:</label>
                        <input
                            name='name'
                            className='form-control rounded-0'
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                        />
                    </div>

                    <div className='box05'>
                        <label>Description:</label>
                        <input
                            name='description'
                            className='form-control rounded-0 two-line-input'
                            onChange={(e) => setDescription(e.target.value)}
                            value={description}
                        />
                    </div>

                    <div className='box05'>
                        <label>Rating:</label>
                        <input
                            name='rating'
                            type='number'
                            className='form-control rounded-0'
                            onChange={handleRatingChange}
                            value={rating}
                            min="0"
                            max="5"
                        />
                    </div>

                    <div className='box05'>
                        <label>Price:</label>
                        <input
                            name='price'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setPrice(e.target.value)}
                            value={price}
                        />
                    </div>

                    <button type='submit' className='submit05'>Submit</button>
                </form>
            </div>
        </div>
    );
}

export default AdBookform;
