import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Nav from '../Nav';
import './cart.css';
import { useNavigate } from 'react-router-dom';

function Cart() {
    const [bookData, setBookData] = useState([]);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchBooks = async () => {
            try {
                const response = await axios.get('http://localhost:1500/getcart');
                if (response.data && Array.isArray(response.data)) {
                    const booksWithQuantity = response.data.map(book => ({
                        ...book,
                        quantity: 1,
                    }));
                    setBookData(booksWithQuantity);
                } else {
                    console.error('Invalid response format:', response.data);
                    setError('Invalid response format');
                }
            } catch (error) {
                console.error('Error fetching new books:', error);
                setError('Error fetching new books: ' + error.message);
            }
        };

        fetchBooks();
    }, []);

    const handleRemove = (id) => {
        const isConfirmed = window.confirm('Are you sure you want to delete this Book?');
        if (isConfirmed) {
            axios.delete(`http://localhost:1500/deletecart/${id}`)
                .then(res => {
                    setBookData(bookData.filter(book => book._id !== id));
                })
                .catch(error => {
                    console.error('Error deleting Book:', error);
                    setError('Error deleting Book: ' + error.message);
                });
        }
    };

    const handleBuy = (id, price, quantity) => {
        const totalPrice = price * quantity;
        navigate(`/paymentcard/${id}?price=${totalPrice}`);
    };

    const handleQuantityChange = (id, increment) => {
        setBookData(bookData.map(book => {
            if (book._id === id) {
                const newQuantity = book.quantity + increment;
                return { ...book, quantity: newQuantity > 0 ? newQuantity : 1 };
            }
            return book;
        }));
    };

    return (
        <div className='full08'>
            <Nav />
            <h2 className='h208'>CART</h2>
            <div className='table-container08'>
                <table className='styled-table08'>
                    <thead>
                        <tr>
                            <th>Image</th>
                            <th>Name</th>
                            <th>Author</th>
                            <th>Description</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {bookData.map(book => (
                            <tr key={book._id}>
                                <td><img src={`http://localhost:1500/images/${book.image}`} alt='newbookimage' width={100} height={100} /></td>
                                <td>{book.name}</td>
                                <td>{book.author}</td>
                                <td className='description08'>{book.description}</td>
                                <td>
                                    <button onClick={() => handleQuantityChange(book._id, -1)}>-</button>
                                    <span>{book.quantity}</span>
                                    <button onClick={() => handleQuantityChange(book._id, 1)}>+</button>
                                </td>
                                <td>{book.price * book.quantity}</td>
                                <td>
                                    <button className='action-button08' onClick={() => handleBuy(book._id, book.price, book.quantity)}>Buy</button>
                                    <button className='action-button08' onClick={() => handleRemove(book._id)}>Remove</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Cart;
