import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './AdBookdata.css'
import Nav from '../../Nav';

function AdBookdata() {

  const [newbookData, setNewbookData] = useState([]);
  useEffect(() => {
    const fetchNewBooks = async () => {
      try {
        const response = await axios.get('http://localhost:1500/getnewbook');
        if (response.data && response.data.books) {
          setNewbookData(response.data.books);
        } else {
          console.error('Invalid response format:', response.data);
        }
      } catch (error) {
        console.error('Error fetching new books:', error);
      }
    };

    fetchNewBooks();
  }, []);

  const fetchData = async () => {
    try {
        const response = await axios.get('http://localhost:1300/allemp');
        setEmployeeData(response.data);
        setLoading(false);
    } catch (error) {
        console.error('Error fetching employee data:', error);
        setError('Error fetching employee data: ' + error.message);
        setLoading(false);
    }
};

const handleDeleteConfirmation = (id) => {
    const isConfirmed = window.confirm('Are you sure you want to delete this Employee?');
    if (isConfirmed) {
        handleDelete(id);
    }
};

const handleDelete = (id) => {
    axios.delete(`http://localhost:1300/deleteemp/${id}`)
        .then(res => {
            setEmployeeData(employeeData.filter(employee => employee._id !== id));
        })
        .catch(error => {
            console.error('Error deleting Employee:', error);
            setError('Error deleting employee: ' + error.message);
        });
};
  return (
    <div className='full06'>
      <Nav />
      <h2 className='h206'>NEW ARRIVALS</h2>
      <div className='table-container'>
        <table className='styled-table'>
          <thead>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>ID</th>
              <th>Description</th>
              <th>Rating</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {/* Conditional rendering */}
            {newbookData && newbookData.map((newbook, index) => (
              <tr key={index}>
                <td><img src={`http://localhost:1500/images/${newbook.image}`} alt='newbookimage' width={100} height={100} /></td>
                <td>{newbook.name}</td>
                <td>{newbook.id}</td>
                <td className='description05'>{newbook.description}</td>
                <td>{newbook.rating}</td>
                <td>{newbook.price}</td>
                <td>
                  <Link to={`/empupdate/${employee._id}`}><button className='action-button'>Update</button></Link>
                  <button className='action-button' onClick={() => handleDeleteConfirmation(employee._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

      </div>

    </div>
  )
}

export default AdBookdata
