import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Signup() {
    const [name,setName]=useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');


  const handleSubmit = (e) => {
    e.preventDefault();
    // Your signup form submission logic goes here
    console.log('Form submitted!');
  };

  return (
    <div className='full01'>
      <h2 className='h201'>SIGNUP FORM</h2>
      <div className='container01'>
        <form className='form01' onSubmit={handleSubmit}>
          <div className='box01'>
            <label>Name:</label>
            <input
            placeholder='Enter Name'
            autoComplete='on'
            name='name'
            className='form-control rounded-0'
              onChange={(e) => setName(e.target.value)}
            />
            <label>E-mail:</label>
            <input
              placeholder='Enter email'
              autoComplete='off'
              name='email'
              className='form-control rounded-0'
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className='box01'>
            <label>Password:</label>
            <input
              placeholder='Enter password'
              autoComplete='off'
              name='password' // Changed name attribute to 'password'
              type='password'
              className='form-control rounded-0'
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type='submit' className='signup01'>Signup</button>
          </div>
        </form>
        <p>Already have an account?</p>
        <Link to="/login" className='login01'>Login</Link>
      </div>
    </div>
  );
}

export default Signup;
