import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function AdBookUpdate() {
  const { id } = useParams();
  const navigate = useNavigate();
  
  const [bookid, setBookid]= useState('');
  const [name, setName] = useState('');
  const [rating, setRating] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:1500/newupdatebook/${id}`);
        const newBookData = response.data.book; a
        setName(newBookData.name);
        setRating(newBookData.rating);
        setPrice(newBookData.price)
        setDescription(newBookData.description);
      } catch (error) {
        console.error('Error fetching book data:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('image', file);
      formData.append('id', id);
      formData.append('name', name);
      formData.append('description', description);
      formData.append('rating', rating);
      formData.append('price', price);

      const config = {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      };

      await axios.put(`http://localhost:1500/updatenewbook/${id}`, formData, config);
      navigate('/newbookdata');
    } catch (error) {
      console.error('Error updating book:', error);
    }
  };

  const handleCancel = (e) => {
    e.preventDefault();
    navigate('/newbookdata');
  };

  const handleRatingChange = (e) => {
    const value = Math.min(Math.max(0, parseInt(e.target.value)), 5);
    setRating(value);
  };

  return (
    <div className='full05'>
      <div className='container05'>
        <h2 className='h205'>BOOK UPDATE FORM</h2>
        <form encType="multipart/form-data" onSubmit={handleUpdate}>
       
        <div className='box05'>
                        <label htmlFor="image">Image:</label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            accept="image/*"
                            onChange={(e) => setFile(e.target.files[0])}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book ID:</label>
                        <input
                            name='id'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setBookid(e.target.value)}
                            value={bookid}
                        />
                    </div>



                    <div className='box05'>
                        <label>Book Name:</label>
                        <input
                            name='name'
                            className='form-control rounded-0'
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                        />
                    </div>

                    <div className='box05'>
                        <label>Description:</label>
                        <input
                            name='description'
                            className='form-control rounded-0 two-line-input'
                            onChange={(e) => setDescription(e.target.value)}
                            value={description}
                        />
                    </div>

                    <div className='box05'>
                        <label>Rating:</label>
                        <input
                            name='rating'
                            type='number'
                            className='form-control rounded-0'
                            onChange={handleRatingChange}
                            value={rating}
                            min="0"
                            max="5"
                        />
                    </div>

                    <div className='box05'>
                        <label>Price:</label>
                        <input
                            name='price'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setPrice(e.target.value)}
                            value={price}
                        />
                    </div>

          <button type='submit' className='submit05'>Update</button>
          <button type='button' className='submit05' onClick={handleCancel}>Cancel</button>
        </form>
      </div>
    </div>
  );
}

export default AdBookUpdate;
