import React from 'react'
import './login.css'

function Login() {
  const [email,setEmail]=useState()
  const [password,setPassword]=useState()
  const navigate=useNavigate()


  axios.defaults.withCredentials=true

  const handleSubmit = (e) => {
      e.preventDefault();
      axios.post('http://localhost:1500/login', { email, password })
          .then(res => {
              console.log(res.data);
              if (res.data.status === "success") {
                  if (res.data.role === "admin") {
                      navigate("/dashboard");
                  } else {
                      navigate('/home');
                  }
              }
          })
          .catch(err => console.log(err));
  };
  return (
    <div className='full01'>
          <h2 className='h201'>LOGIN FORM</h2>
      <div className='container01'>
      
        <form className='form01' onSubmit={handleSubmit}>

            <div className='box01'>
            <label>E-mail ID:</label>
            <input
            placeholder='enter email'
            autoComplete='off'
            name='email'
            className='form-control rounded-0'
            onChange={(e)=>setEmail(e.target.value)}
            />
            </div>

            <div className='box01'>
            <label>Password:</label>
            <input 
             placeholder='enter password'
             autoComplete='off'
             name='email'
             className='form-control rounded-0'
             onChange={(e)=>setPassword(e.target.value)}
              />
            
            
            <button type='submit'className='login01'>Login</button>
            <button className='signup01'>Sign up</button>
            </div>
        </form>
        <p>Do not have account ?</p>
            <Link to="/" className='btn btn-default border w-100 bg-light'>Sign up</Link>
         

      </div>
    </div>
  )
}

export default Login
