import React, { useState } from 'react';
import './nav.css';

function Nav() {
    const [openProfile, setOpenProfile] = useState(false);

    const handleDropdown = () => {
        setOpenProfile(!openProfile); // Toggles the state
    };

    const DropdownProfile = () => {
        return (
            <div className='flex flex-col dropDownProfile'>
                <ul className=' ul02'>
                    <ul>About</ul>
                    <ul>contact</ul>
                    <ul>Logout</ul>
                </ul>
            </div>
        );
    };

    return (
        <div>
            <div className='nav02'>
                <img className='img02' src='http://www.freepngclipart.com/download/reading/78273-vector-open-book-hand-painted-free-download-png-hd.png' alt="Logo" />
                <h3 className='h302'>HOME</h3>
                <div className='navright02'>
                    <h5 className='h502'>My cart</h5>
                    <img className='img202' src='https://kalytapartners.com/images/icon-menu.png' alt='menu' height={30} onClick={handleDropdown}/>
                    {openProfile && <DropdownProfile />}
                </div>
            </div>
            <hr></hr>
        </div>
    );
}

export default Nav;
