import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [id, setId] = useState(''); // Using user ID instead of email
  const [password, setPassword] = useState('');
  const [incorrectPassword, setIncorrectPassword] = useState(false); // State for incorrect password alert
  const navigate = useNavigate();

  axios.defaults.withCredentials = true;

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post(`http://localhost:3000/login/${id}`, { password }) // Using user ID in the URL
      .then(res => {
        console.log(res.data);
        if (res.data.status === "success") {
          navigate(`/home/${id}`); // Redirect to user-specific home page
        } else {
          setIncorrectPassword(true); // Set incorrect password alert to true
        }
      })
      .catch(err => {
        console.log(err);
        setIncorrectPassword(true); // Set incorrect password alert to true
      });
  };

  return (
    <div className='full01'>
      <h2 className='h201'>LOGIN FORM</h2>
      <div className='container01'>

        <form className='form01' onSubmit={handleSubmit}>

          <div className='box01'>
            <label>E-mail ID:</label>
            <input
          
              name='email'
              className='form-control rounded-0'
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div className='box01'>
            <label>Password:</label>
            <input
              autoComplete='off'
              name='email'
              type='password' 
              className='form-control rounded-0'
              onChange={(e) => setPassword(e.target.value)}
            />

            <button type='submit' className='login01'>Login</button>
          </div>
        </form>
        {incorrectPassword && <p className='alert01'>Incorrect email or password. Please try again.</p>} {/* Display alert for incorrect password */}
        <p className='q01'>Do not have an account ?</p>
        <Link to="/" className='signup01'>Sign up</Link>


      </div>
    </div>
  );
}

export default Login;
