import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Nav from '../Nav';
import './home.css';
import image04 from '../../images/04.jpg';
import image05 from '../../images/08.png';
import image09 from '../../images/09.png';
import image10 from '../../images/10.png';
import image11 from '../../images/11.png';
import image12 from '../../images/12.png';
import image15 from '../../images/15.jpg';


function Home() {
  const [newbookData, setNewbookData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showAll, setShowAll] = useState(false);
  
  useEffect(() => {
    const fetchNewBooks = async () => {
      try {
        const response = await axios.get('http://localhost:1500/getnewbook');
        if (response.data && response.data.books) {
          setNewbookData(response.data.books);
        } else {
          console.error('Invalid response format:', response.data);
        }
      } catch (error) {
        console.error('Error fetching new books:', error);
      }
    };

    fetchNewBooks();
  }, []);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % 3); // Adjust based on the number of images
    }, 5000);

    return () => clearInterval(interval);
  }, []);
  const visibleBooks = showAll ? newbookData : newbookData.slice(0, 4);

  return (
    <div className='home-container'>
      <div className='nav03'>
        <Nav />
      </div>

      <div className='container03'>
        <div className="carousel" style={{ transform: `translateX(-${currentIndex * (100 / 3)}%)` }}>
          <img src={image04} alt='image04' />
          <img src={image05} alt='image05' />
          <img src={image15} alt='image15'/>
        </div>
      </div>

      
      <hr className='hr03'></hr>
      <div className='badges03'>
        <div className='badge1'>
          <img src={image09} alt='image09' className='image09' />
          <h4>Award wins</h4>
        </div>

        <div className='badge2'>
          <img src={image10} alt='image10' className='image10' />
          <h4>International</h4>
        </div>

        <div className='badge3'>
          <img src={image11} alt='image11' className='image11' />
          <h4>Best seller</h4>
        </div>

        <div className='badge4'>
          <img src={image12} alt='image12' className='image12' />
          <h4>New Arrivals</h4>
        </div>
      </div>

      <h2>NEW ARRIVALS</h2>
      <div className='card-container03'>
                {/* Conditional rendering */}
                {visibleBooks.map((newbook, index) => (
                    <div key={index} className='card03'>
                        <img src={`http://localhost:1500/images/${newbook.image}`} alt='newbookimage' width={200} height={150} />
                        <h3>{newbook.name}</h3>
                        <p>{newbook.id}</p>
                        <p className='description03'>{newbook.description}</p>
                        <div className='rating03'>
                        
                        <img className='ratingstar03' src='http://clipart-library.com/img/2184497.png' alt='image'/>
                        <p>{newbook.rating}</p>
                       </div>
                        <p>{newbook.price}</p>
                        <button className='addtocartbutton03'>Add to cart</button>
                        <button className='buynowbutton03'>Buy now</button>
                    </div>
                ))}
            </div>
            {!showAll && (
                <button className='seeall03' onClick={() => setShowAll(true)}>See All</button>
            )}
    </div>
  );
}

export default Home;
