import React, { useState } from 'react';
import './Adbookform.css';
import axios from 'axios';

function AdBookform() {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [id, setId] = useState('');
    const [rating, setRating] = useState('');
    const [price, setPrice] = useState('');
    const [file, setFile] = useState(null);
    const [category, setCategory] = useState(''); // State for category

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Basic validation
            if (!name || !id || !description || !rating || !price || !file || !category) {
                console.error('Please fill in all fields');
                return;
            }

            // Create FormData object
            const formData = new FormData();
            formData.append('name', name);
            formData.append('id', id);
            formData.append('description', description);
            formData.append('rating', rating);
            formData.append('price', price);
            formData.append('category', category); // Append category to FormData
            formData.append('image', file);

            // Send POST request with FormData
            const response = await axios.post('http://localhost:1500/postnewbook', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log('New book created:', response.data);

            // Clear form fields after successful submission
            setTimeout(() => {
                setName('');
                setDescription('');
                setId('');
                setRating('');
                setPrice('');
                setCategory('');
                setFile(null); // Clear the file state
            }, 100); // Adjust the delay time as needed

        } catch (error) {
            console.error('Error creating new book:', error);
        }
    };

    const handleRatingChange = (e) => {
        const value = Math.min(Math.max(0, parseInt(e.target.value)), 5);
        setRating(value);
    };

    return (
        <div className='full05'>
            <div className='container05'>
                <h2 className='h205'>BOOK ENTRY FORM</h2>
                <form onSubmit={handleSubmit} encType="multipart/form-data">

                    <div className='box05'>
                        <label htmlFor="image">Image:</label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            accept="image/*"
                            onChange={(e) => setFile(e.target.files[0])}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book ID:</label>
                        <input
                            name='id'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setId(e.target.value)}
                            value={id}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book Name:</label>
                        <input
                            name='name'
                            className='form-control rounded-0'
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                        />
                    </div>

                    <div className='box05'>
                        <label>Description:</label>
                        <input
                            name='description'
                            className='form-control rounded-0 two-line-input'
                            onChange={(e) => setDescription(e.target.value)}
                            value={description}
                            maxLength={80} // Set maximum length to 80 characters
                        />
                    </div>

                    <div className='box05'>
                        <label>Rating:</label>
                        <input
                            name='rating'
                            type='number'
                            className='form-control rounded-0'
                            onChange={handleRatingChange}
                            value={rating}
                            min="0"
                            max="5"
                        />
                    </div>

                    <div className='box05'>
                        <label>Price:</label>
                        <input
                            name='price'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setPrice(e.target.value)}
                            value={price}
                        />
                    </div>
                    
                    <div className='box05'>
                        <label htmlFor="category">Category:</label>
                        <select
                            id="category"
                            name="category"
                            onChange={(e) => setCategory(e.target.value)}
                            value={category}
                        >
                            <option value="">Select Category</option>
                            <option value="new-arrivals">New arrivals</option>
                            <option value="international">International</option>
                            <option value="f">Award wins</option>
                            {/* Add more options as needed */}
                        </select>
                    </div>

                    <button type='submit' className='submit05'>Submit</button>
                </form>
            </div>
        </div>
    );
}

export default AdBookform;
