import React, { useState } from 'react';
import Nav from '../Nav';
import './home.css';
import image04 from '../../images/04.jpg';
import image05 from '../../images/05.g';
import image09 from '../../images/09.png';
import image10 from '../../images/10.png';
import image11 from '../../images/11.png';
import image12 from '../../images/12.png';

function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = [image04, image05];

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className='home-container'>
      <div className='nav03'>
        <Nav />
      </div>

      <div className='container03'>
        <button className='arrow left-arrow' onClick={prevImage}>&#10094;</button>
        <img src={images[currentImageIndex]} alt='image' />
        <button className='arrow right-arrow' onClick={nextImage}>&#10095;</button>
      </div>

      <hr className='hr03'></hr>
      <div className='badges03'>
        <div className='badge1'>
          <img src={image09} alt='image09' className='image09' />
          <h4>Award wins</h4>
        </div>

        <div className='badge2'>
          <img src={image10} alt='image10' className='image10' />
          <h4>International</h4>
        </div>
        
        <div className='badge3'>
          <img src={image11} alt='image11' className='image11' />
          <h4>Best seller</h4>
        </div>

        <div className='badge4'>
          <img src={image12} alt='image12' className='image12'/>
          <h4>New Arrivals</h4>
        </div>
      </div>
    </div>
  );
}

export default Home;
