import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './login.css';
import 'react-toastify/dist/ReactToastify.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate(); // Get the navigate function from React Router

  const login = async () => {
    const user = {
      email,
      password,
    };

    try {
      setLoading(true);
      const response = await axios.post('http://localhost:4000/login', user);
      setLoading(false);

      const userData = {
        name: response.data.name,
        token: response.data.token,
      };

      localStorage.setItem('currentUser', JSON.stringify(userData));
      console.log('Login successful', response.data);

      // Check if the user is an admin
      if (response.data.isAdmin) {
        navigate('/admin/' + response.data._id); // Navigate to admin page
      } else {
        navigate('/frontpage/' + response.data._id); // Navigate to frontpage for regular users
      }

    } catch (err) {
      console.error('Login error:', err);

      if (err.response && err.response.status === 401) {
        toast.error('Invalid email or password. Please try again.'); // Display toastify error message for incorrect password
      } else {
        toast.error('An error occurred while logging in.'); // Display toastify error message for other login errors
      }

      setLoading(false);
    }
  };

  return (
    <div className='full01'>
      <h2 className='h201'>LOGIN FORM</h2>
      <div className='container01'>

        <form className='form01' onSubmit={handleSubmit}>

          <div className='box01'>
            <label>E-mail ID:</label>
            <input
            
              name='email'
              className='form-control rounded-0'
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div className='box01'>
            <label>Password:</label>
            <input
              autoComplete='off'
              name='email'
              type='password' 
              className='form-control rounded-0'
              onChange={(e) => setPassword(e.target.value)}
            />

            <button type='submit' className='login01'>Login</button>
          </div>
        </form>
        {incorrectPassword && <p className='alert01'>Incorrect email or password. Please try again.</p>} {/* Display alert for incorrect password */}
        <p className='q01'>Do not have an account ?</p>
        <Link to="/" className='signup01'>Sign up</Link>


      </div>
    </div>
  );
}

export default Login;
