import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Nav from '../../Nav';
import { useNavigate } from 'react-router-dom'; // Import Link and useNavigate for routing

function Orders() {
    const [newbookData, setNewbookData] = useState([]);
    const [error, setError] = useState(null);
    const navigate = useNavigate(); // Initialize navigate hook

    useEffect(() => {
        const fetchNewBooks = async () => {
            try {
                const response = await axios.get('http://localhost:1500/ordered');
                if (response.data && response.data.books) {
                    setNewbookData(response.data.books);
                } else {
                    console.error('Invalid response format:', response.data);
                }
            } catch (error) {
                console.error('Error fetching new books:', error);
                setError('Error fetching new books: ' + error.message);
            }
        };

        fetchNewBooks();
    }, []);

    const handleDelete = (id) => {
        axios.delete(`http://localhost:1500/deletenewbook/${id}`)
            .then(res => {
                setNewbookData(newbookData.filter(newbook => newbook._id !== id));
            })
            .catch(error => {
                console.error('Error deleting Book:', error);
                setError('Error deleting Book: ' + error.message);
            });
    };

    const handleUpdate = (id) => {
        navigate(`/newbookupdate/${id}`); // Navigate to update page with book ID
    };

    return (
        <div className='full06'>
            <Nav />
            <h2 className='h206'>NEW ARRIVALS</h2>
            <div className='table-container'>
                {error && <p className='error'>{error}</p>}
                <table className='styled-table'>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Address</th>
                            <th>Price</th>
                            <th>Debitcard No</th>
                            <th>Expiry date</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {newbookData.length > 0 ? (
                            newbookData.map(ordered => (
                                <tr key={ordered._id}>
                                    <td>{ordered.name}</td>
                                    <td>{ordered.email}</td>
                                    <td className='description05'>{ordered.address}</td>
                                    <td>{ordered.price}</td>
                                    <td>{ordered.debitcardno}</td>
                                    <td>{ordered.expiry}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="7">No new books found</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Orders;
