import React from 'react';
import { useState } from 'react';
import Nav from '../Nav';
import './home.css';
import image04 from '../../images/04.jpg';
import image05 from '../../images/08.png';
import image09 from '../../images/09.png';
import image10 from '../../images/10.png';
import image11 from '../../images/11.png';
import image12 from '../../images/12.png';

function Home() {

  const [newbookData, setNewbookData] = useState([]);

  useEffect(() => {
    // Function to fetch new book data when component mounts
    const fetchNewBooks = async () => {
      try {
        // Make GET request to fetch new book data from the backend
        const response = await axios.get('http://localhost:1500/getnewbook');
        setNewbookData(response.data.books); // Set the retrieved new book data in state
      } catch (error) {
        console.error('Error fetching new books:', error);
      }
    };

    fetchNewBooks(); // Call the function to fetch new book data
  }, []);
  return (
    <div className='home-container'>
      <div className='nav03'>
        <Nav />
      </div>

      <div className='container03'>
        <img src={image04} alt='image' />
        <img src={image05} alt='image' />
      </div>

      <hr className='hr03'></hr>
      <div className='badges03'>
        <div className='badge1'>
          <img src={image09} alt='image09' className='image09' />
          <h4>Award wins</h4>
        </div>

        <div className='badge2'>
          <img src={image10} alt='image10' className='image10' />
          <h4>International</h4>
        </div>
        
        <div className='badge3'>
          <img src={image11} alt='image11' className='image11' />
          <h4>Best seller</h4>
        </div>

        <div className='badge4'>
          <img src={image12} alt='image12' className='image12'/>
          <h4>New Arrivals</h4>
        </div>
      </div>

      <h2>NEW ARRIVALS</h2>
                <div className='card-container'>
                    {newbookData.map((newbook, index) => (
                        <div key={index} className='card'>
                            <img src={`http://localhost:1300/images/${newbook.image}`} alt='newbookimage' width={'100px'} height={'100px'} />
                            <h3>{newbook.name}</h3>
                            <p>{newbook.id}</p>
                            <p>{newbook.description}</p>
                            <p>{newbook.rating}</p>
                            <p>{newbook.price}</p>
                            <Link to={`/empupdate/${employee._id}`}><button>Update</button></Link>
                            <button onClick={() => handleDeleteConfirmation(employee._id)}>Delete</button>
                        </div>
                    ))}
                </div>
    </div>
  );
}

export default Home;
