                            onChange={(e) => setRating(e.target.value)}
                            value={rating}
                        />
                    </div>

                    <div className='box05'>
                        <label>Price:</label>
                        <input
                            name='price'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setPrice(e.target.value)}
                            value={price}
                        />
                    </div>

                    <button type='submit' className='submit05'>Submit</button>
                </form>
            </div>
        </div>
    );
}

