import React, { useState } from 'react';
import './paymentcard.css';
import { useLocation } from 'react-router-dom';
import queryString from 'query-string';
import image20 from '../../images/20.png';
import image21 from '../../images/21.png';

const Payment = ({ book }) => { // Add book prop
  const location = useLocation();
  const { price } = queryString.parse(location.search);

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [debitCardNumber, setDebitCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log('Form submitted with the following details:');
    console.log({ name, email, address, price, debitCardNumber, expiryDate, cvv });
  };

  return (
    <div>
      <h3 className='h309'>PAYMENT FORM</h3>
      <div className='payment-container09'>
        <div className="book-details">
          <img src={book.image} alt={book.title} />
          <h4>{book.title}</h4>
          <p>{book.author}</p>
        </div>
        <div className="payment-card">
          <form onSubmit={handleSubmit}>
            {/* ...rest of the form */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default Payment;
