import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';

function International() {
  const [interbookData, setInterbookData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:1500/getinterbook');
        if (response.data?.interbook) {
          setInterbookData(response.data.interbook);
        } else {
          console.error('Invalid international books response format:', response.data);
        }
      } catch (error) {
        console.error('Error fetching international books:', error);
      }
    };

    fetchBooks();
  }, []);

  const addToCart = async (book) => {
    try {
      const imageResponse = await axios.get(`http://localhost:1500/images/${book.image}`, {
        responseType: 'arraybuffer',
      });

      const imageBlob = new Blob([imageResponse.data], { type: 'image/jpeg' });
      const imageFile = new File([imageBlob], book.image, { type: 'image/jpeg' });

      const formData = new FormData();
      formData.append('name', book.name);
      formData.append('author', book.author);
      formData.append('description', book.description);
      formData.append('price', book.price);
      formData.append('image', imageFile);

      await axios.post('http://localhost:1500/postcart', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      Swal.fire({
        toast: true,
        position: 'top-end',
        icon: 'success',
        title: '',
        text: `Added to cart!`,
        showConfirmButton: false,
        timer: 1500,
      });
    } catch (error) {
      console.error('Error adding to cart:', error);
      Swal.fire({
        position: 'top',
        icon: 'error',
        title: 'Oops...',
        text: 'Failed to add to cart. Please try again.',
      });
    }
  };

  const handleBuy = (id, price, name) => {
    navigate(`/paymentcard/${id}?price=${price}&name=${encodeURIComponent(name)}`);
  };

  return (
    <div className='international-container'>
      <h2>International Books</h2>
      <div className='card-container'>
        {interbookData.map((interbook, index) => (
          <div key={index} className='card'>
            <img src={`http://localhost:1500/images/${interbook.image}`} alt='interbookimage' width={140} height={150} />
            <h5>{interbook.name}</h5>
            <p>{interbook.author}</p>
            <p className='description'>{interbook.description}</p>
            <p className='price'>{interbook.price}</p>
            <button className='addtocartbutton' onClick={() => addToCart(interbook)}>Add to cart</button>
            <button className='buynowbutton' onClick={() => handleBuy(interbook._id, interbook.price, interbook.name)}>Buy now</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default International;
