import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function AdBookUpdate() {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [id, setId] = useState('');
    const [rating, setRating] = useState('');
    const [price, setPrice] = useState('');
    const [file, setFile] = useState(null);
    const navigate = useNavigate();

    function NewBookUpdate() {
        let { id } = useParams(); // Retrieve the id parameter from the URL
      
        // Rest of the component code
      }

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:1500/getbook/${id}`);
                const bookData = response.data.book;
                setName(bookData.name);
                setDescription(bookData.description);
                setRating(bookData.rating);
                setPrice(bookData.price);
            } catch (error) {
                console.error('Error fetching book data:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('image', file);
            formData.append('name', name);
            formData.append('description', description);
            formData.append('rating', rating);
            formData.append('price', price);

            const config = {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            };

            await axios.put(`http://localhost:1500/updatebook/${id}`, formData, config);
            navigate('/newbookdata');
        } catch (error) {
            console.error('Error updating book:', error);
        }
    };

    const handleCancel = (e) => {
        e.preventDefault();
        navigate('/newbookdata');
    };

    const handleRatingChange = (e) => {
        const value = Math.min(Math.max(0, parseInt(e.target.value)), 5);
        setRating(value);
    };

    return (
        <div className='full05'>
            <div className='container05'>
                <h2 className='h205'>BOOK ENTRY FORM</h2>
                <form encType="multipart/form-data">

                    <div className='box05'>
                        <label htmlFor="image">Image:</label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            accept="image/*"
                            onChange={(e) => setFile(e.target.files[0])}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book ID:</label>
                        <input
                            name='id'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setId(e.target.value)}
                            value={id}
                        />
                    </div>

                    <div className='box05'>
                        <label>Book Name:</label>
                        <input
                            name='name'
                            className='form-control rounded-0'
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                        />
                    </div>

                    <div className='box05'>
                        <label>Description:</label>
                        <input
                            name='description'
                            className='form-control rounded-0 two-line-input'
                            onChange={(e) => setDescription(e.target.value)}
                            value={description}
                        />
                    </div>

                    <div className='box05'>
                        <label>Rating:</label>
                        <input
                            name='rating'
                            type='number'
                            className='form-control rounded-0'
                            onChange={handleRatingChange}
                            value={rating}
                            min="0"
                            max="5"
                        />
                    </div>

                    <div className='box05'>
                        <label>Price:</label>
                        <input
                            name='price'
                            type='text'
                            className='form-control rounded-0'
                            onChange={(e) => setPrice(e.target.value)}
                            value={price}
                        />
                    </div>

                    <button type='submit' className='submit05' onClick={handleUpdate}>Update</button>
                    <button type='button' className='submit05' onClick={handleCancel}>Cancel</button>
                </form>
            </div>
        </div>
    );
}

export default AdBookUpdate;
