import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Nav from '../Nav.js';

function Newarrivals() {
  const [newbookData, setNewbookData] = useState([]);

  useEffect(() => {
    const fetchNewBooks = async () => {
      try {
        const response = await axios.get('http://localhost:1500/getnewbook');
        if (response.data && response.data.books) {
          setNewbookData(response.data.books);
        } else {
          console.error('Invalid response format:', response.data);
        }
      } catch (error) {
        console.error('Error fetching new books:', error);
      }
    };

    fetchNewBooks();
  }, []); // Empty dependency array ensures useEffect runs only once

  return (
    <div>
      <div className='nav03'>
        <Nav />
      </div>

      <div className='n'>
      <h2>NEW ARRIVALS</h2>

        <div className='card-container03'>
          {/* Conditional rendering */}
          {newbookData.map((newbook, index) => (
            <div key={index} className='card03'>
              <img src={`http://localhost:1500/images/${newbook.image}`} alt='newbookimage' width={200} height={150} />
              <h3>{newbook.name}</h3>
              <p>{newbook.id}</p>
              <p className='description03'>{newbook.description}</p>

              <div className='rating03'>
                <img className='ratingstar03' src='http://clipart-library.com/img/2184497.png' alt='image' height={18} />
                <p className='ratingvalue03'>{newbook.rating}</p>
              </div>

              <p className='price03'>{newbook.price}</p>
              <button className='addtocartbutton03'>Add to cart</button>
              <button className='buynowbutton03'>Buy now</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Newarrivals;
